import React from "react";

const dummyNFTs = [
  {
    id: 1,
    title: "Cyber Cat",
    image:
      "https://i.seadn.io/gae/IeH4k0RIbfqO2ta4FlwCEeKnflAfJq8VKJ7i_dXK9H_B7cXf4eZdYw4-pFbVXYKQ7C6iL2Noeb1zjvRxPQq6tK69BkQ0WRk3Xhbi?auto=format&dpr=1&w=1000",
    price: "0.08 ETH",
  },
  {
    id: 2,
    title: "Alien Punk",
    image:
      "https://cryptopunks.app/cryptopunks/cryptopunk2835.png",
    price: "1.2 ETH",
  },
  {
    id: 3,
    title: "Pixel Samurai",
    image:
      "https://i.seadn.io/gae/HN-QZCnqWkP2CmjsA_kTTij19f9ZFeV2TSC_jc_Xn8SPgxMn4aqZ9DQ9l-r2_B_jTjA6cqv0bJfbw4kKbZacFMI0niBIK8EKnPsl?auto=format&dpr=1&w=1000",
    price: "0.45 ETH",
  },
];

function Explore() {
  return (
    <div style={{ padding: "30px" }}>
      <h2 style={{ textAlign: "center", fontSize: "2rem" }}>Explore NFTs</h2>
      <p style={{ textAlign: "center", marginBottom: "30px" }}>
        Browse and purchase unique digital assets.
      </p>

      <div
        style={{
          display: "flex",
          gap: "20px",
          flexWrap: "wrap",
          justifyContent: "center",
        }}
      >
        {dummyNFTs.map((nft) => (
          <div
            key={nft.id}
            style={{
              width: "250px",
              border: "1px solid #ddd",
              borderRadius: "10px",
              padding: "15px",
              textAlign: "center",
              boxShadow: "0px 2px 8px rgba(0,0,0,0.1)",
            }}
          >
            <img
              src={nft.image}
              alt={nft.title}
              style={{ width: "100%", borderRadius: "8px" }}
            />
            <h3 style={{ marginTop: "15px" }}>{nft.title}</h3>
            <p style={{ color: "#4F46E5", fontWeight: "bold" }}>{nft.price}</p>
            <button
              style={{
                marginTop: "10px",
                padding: "8px 16px",
                backgroundColor: "#4F46E5",
                color: "#fff",
                border: "none",
                borderRadius: "6px",
                cursor: "pointer",
              }}
              onClick={() => alert(`Purchase ${nft.title} - Coming soon!`)}
            >
              Purchase
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Explore;
