import React, { useEffect, useState } from 'react';

function App() {
  const [nfts, setNfts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch NFT data from the API
    fetch('/api/nfts')
      .then(res => {
        if (!res.ok) {
          throw new Error('Network response was not ok');
        }
        return res.json();
      })
      .then(data => {
        setNfts(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching NFTs:', err);
        setError('Failed to fetch NFT data.');
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div className="App"><p>Loading NFTs...</p></div>;
  }

  if (error) {
    return <div className="App"><p>{error}</p></div>;
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>NFT Marketplace</h1>
      </header>
      <main>
        <h2>Available NFTs</h2>
        {nfts.length > 0 ? (
          <div className="nft-grid">
            {nfts.map(nft => (
              <div className="nft-card" key={nft.id}>
                <img 
                  src={nft.image} 
                  alt={nft.name} 
                  className="nft-image"
                  onError={(e)=>{e.target.onerror = null; e.target.src="https://via.placeholder.com/220"}}
                />
                <h3>{nft.name}</h3>
                <p>Price: {nft.price}</p>
                <button>Buy Now</button>
              </div>
            ))}
          </div>
        ) : (
          <p>No NFTs available at the moment.</p>
        )}
      </main>
    </div>
  );
}

export default App;
