// /client/src/pages/Explore.js
import React, { useState } from "react";
import { useNFTContext } from "../context/NFTContext";

const dummyNFTs = [
    {
      id: 1,
      name: "Cyber Cat",
      image: "https://coin-images.coingecko.com/nft_contracts/images/4821/large/cyber-cat.png?1723501409",
      price: "0.08 ETH",
    },
    {
      id: 2,
      name: "Alien Punk",
      image: "https://www.larvalabs.com/cryptopunks/cryptopunk2835.png",
      price: "1.2 ETH",
    },
    {
      id: 3,
      name: "Pixel Samurai",
      image: "data:image/jpeg;base64,",
      price: "0.45 ETH",
    },
    {
      id: 4,
      name: "Robo Gorilla",
      image: "https://i.seadn.io/gcs/files/b19f1ff7ac8a99eeac5d7a5e5b83b726.png?auto=format&dpr=1&w=500",
      price: "0.33 ETH",
    },
    {
      id: 5,
      name: "Mystic Fox",
      image: "https://i.seadn.io/gcs/files/7a94669ff4fc5bafc9b0d2f124af4b46.png?auto=format&dpr=1&w=500",
      price: "0.65 ETH",
    },
    {
      id: 6,
      name: "Space Ape",
      image: "https://i.seadn.io/gcs/files/e6d7f3130fef6071cf5464d0dfc36495.png?auto=format&dpr=1&w=500",
      price: "0.92 ETH",
    },
    {
      id: 7,
      name: "Pixel Wizard",
      image: "https://i.seadn.io/gcs/files/e06f8f005ac737d5218c5770cf09b08f.png?auto=format&dpr=1&w=500",
      price: "0.49 ETH",
    },
    {
      id: 8,
      name: "Neon Skull",
      image: "https://i.seadn.io/gcs/files/11b1de7cfe94e7648e2d616cc0e5ad84.png?auto=format&dpr=1&w=500",
      price: "0.73 ETH",
    },
    {
      id: 9,
      name: "Cyber Samurai",
      image: "https://i.seadn.io/gcs/files/05599aa4dd81f8685f8e4292e0ff2325.png?auto=format&dpr=1&w=500",
      price: "1.1 ETH",
    },
    {
      id: 10,
      name: "Pixel Panda",
      image: "https://i.seadn.io/gcs/files/b2e15b7ac059988f7f94f97a1c6d37f3.png?auto=format&dpr=1&w=500",
      price: "0.38 ETH",
    },
  ];



function Explore() {
const { setPurchasedNFTs, purchasedNFTs } = useNFTContext();
  const [cart, setCart] = useState([]);
  const [snackbar, setSnackbar] = useState("");
  const [showCheckout, setShowCheckout] = useState(false);
  const [walletAddress, setWalletAddress] = useState("");
  const [transactionId, setTransactionId] = useState("");

  const handleBuy = (nft) => {
    setCart([...cart, nft]);
    setSnackbar(`Added '${nft.name}' to your cart.`);
    setTimeout(() => setSnackbar(""), 3000);
  };

  const totalETH = cart.reduce((sum, item) => sum + parseFloat(item.price), 0);

  const handleCheckout = () => {
    setPurchasedNFTs([...purchasedNFTs, ...cart]);
    if (!walletAddress) {
      alert("Please enter a wallet address.");
      return;
    }
    const txId = "TX" + Math.floor(Math.random() * 100000000);
    setTransactionId(txId);
    setCart([]);
    setShowCheckout(false);
    setSnackbar(`Payment successful! Transaction ID: ${txId}`);
    setWalletAddress("");
    setTimeout(() => setSnackbar(""), 5000);
  };

  return (
    <div style={{ display: "flex", padding: "30px" }}>
      <div style={{ flex: 3 }}>
        <h2 style={{ textAlign: "center", fontSize: "2rem" }}>Explore NFTs</h2>
        <p style={{ textAlign: "center", marginBottom: "30px" }}>
          Browse and purchase unique digital assets.
        </p>

        {snackbar && (
          <div
            style={{
              backgroundColor: "#4F46E5",
              color: "white",
              padding: "10px",
              textAlign: "center",
              borderRadius: "5px",
              marginBottom: "20px",
            }}
          >
            {snackbar}
          </div>
        )}

        <div
          style={{
            display: "flex",
            gap: "20px",
            flexWrap: "wrap",
            justifyContent: "center",
          }}
        >
          {dummyNFTs.map((nft) => (
            <div
              key={nft.id}
              style={{
                width: "250px",
                border: "1px solid #ddd",
                borderRadius: "10px",
                padding: "15px",
                textAlign: "center",
                boxShadow: "0px 2px 8px rgba(0,0,0,0.1)",
              }}
            >
              <img
                src={nft.image}
                alt={nft.name}
                style={{ width: "100%", borderRadius: "8px" }}
              />
              <h3 style={{ marginTop: "15px" }}>{nft.name}</h3>
              <p style={{ color: "#4F46E5", fontWeight: "bold" }}>{nft.price}</p>
              <button
                style={{
                  marginTop: "10px",
                  padding: "8px 16px",
                  backgroundColor: "#4F46E5",
                  color: "#fff",
                  border: "none",
                  borderRadius: "6px",
                  cursor: "pointer",
                }}
                onClick={() => handleBuy(nft)}
              >
                Add to Cart
              </button>
            </div>
          ))}
        </div>
      </div>

      <div style={{ flex: 1, marginLeft: "30px" }}>
        <h3>Your Cart</h3>
        {cart.length === 0 ? (
          <p>No items yet.</p>
        ) : (
          <>
            <ul>
              {cart.map((item, index) => (
                <li key={index}>
                  {item.name} - {item.price}
                </li>
              ))}
            </ul>
            <p style={{ fontWeight: "bold" }}>Total: {totalETH.toFixed(2)} ETH</p>
            {!showCheckout ? (
              <button
                onClick={() => setShowCheckout(true)}
                style={{ marginTop: "10px", padding: "10px", width: "100%" }}
              >
                Proceed to Checkout
              </button>
            ) : (
              <div style={{ marginTop: "10px" }}>
                <input
                  type="text"
                  placeholder="Enter Wallet Address"
                  value={walletAddress}
                  onChange={(e) => setWalletAddress(e.target.value)}
                  style={{ width: "100%", padding: "8px", marginBottom: "10px" }}
                />
                <button
                  onClick={handleCheckout}
                  style={{ padding: "10px", width: "100%" }}
                >
                  Pay Now
                </button>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default Explore;
