// /client/src/pages/Explore.js
import React, { useState } from "react";

const mockNFTs = [
  {
    id: 1,
    name: "Digital Galaxy",
    image: "https://via.placeholder.com/200",
    price: "0.5 ETH"
  },
  {
    id: 2,
    name: "Cyberpunk Tiger",
    image: "https://via.placeholder.com/200",
    price: "1.2 ETH"
  },
  {
    id: 3,
    name: "Pixel Dragon",
    image: "https://via.placeholder.com/200",
    price: "0.8 ETH"
  }
];

function Explore() {
  const [cart, setCart] = useState([]);
  const [message, setMessage] = useState("");

  const handleBuy = (nft) => {
    setCart((prev) => [...prev, nft]);
    setMessage(`'${nft.name}' added to cart`);
    setTimeout(() => setMessage(""), 3000);
  };

  return (
    <div style={{ display: "flex", padding: "20px" }}>
      {/* Main Content */}
      <div style={{ flex: 3 }}>
        <h2>Explore NFTs</h2>
        {message && (
          <div
            style={{
              margin: "10px 0",
              padding: "10px",
              backgroundColor: "#dff0d8",
              color: "#3c763d",
              borderRadius: "5px"
            }}
          >
            {message}
          </div>
        )}
        <div style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
          {mockNFTs.map((nft) => (
            <div
              key={nft.id}
              style={{
                border: "1px solid #ddd",
                borderRadius: "10px",
                padding: "10px",
                width: "200px"
              }}
            >
              <img
                src={nft.image}
                alt={nft.name}
                style={{ width: "100%", borderRadius: "8px" }}
              />
              <h3>{nft.name}</h3>
              <p>
                <strong>Price:</strong> {nft.price}
              </p>
              <button
                onClick={() => handleBuy(nft)}
                style={{ width: "100%", padding: "8px", cursor: "pointer" }}
              >
                Buy
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* Cart Sidebar */}
      <div
        style={{
          flex: 1,
          marginLeft: "30px",
          border: "1px solid #ccc",
          borderRadius: "8px",
          padding: "15px",
          backgroundColor: "#f9f9f9",
          height: "fit-content"
        }}
      >
        <h3>Your Cart</h3>
        {cart.length === 0 ? (
          <p>No items in cart</p>
        ) : (
          <ul style={{ listStyle: "none", padding: 0 }}>
            {cart.map((item, index) => (
              <li
                key={index}
                style={{
                  borderBottom: "1px solid #ddd",
                  padding: "10px 0"
                }}
              >
                {item.name} - {item.price}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default Explore;
