// /client/src/pages/Explore.js
import React from "react";

const mockNFTs = [
  {
    id: 1,
    name: "Digital Galaxy",
    image: "https://via.placeholder.com/200",
    price: "0.5 ETH"
  },
  {
    id: 2,
    name: "Cyberpunk Tiger",
    image: "https://via.placeholder.com/200",
    price: "1.2 ETH"
  },
  {
    id: 3,
    name: "Pixel Dragon",
    image: "https://via.placeholder.com/200",
    price: "0.8 ETH"
  }
];

function Explore() {
  return (
    <div style={{ padding: "20px" }}>
      <h2>Explore NFTs</h2>
      <div style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
        {mockNFTs.map((nft) => (
          <div key={nft.id} style={{ border: "1px solid #ddd", borderRadius: "10px", padding: "10px", width: "200px" }}>
            <img src={nft.image} alt={nft.name} style={{ width: "100%", borderRadius: "8px" }} />
            <h3>{nft.name}</h3>
            <p><strong>Price:</strong> {nft.price}</p>
            <button style={{ width: "100%", padding: "8px", cursor: "pointer" }}>Buy</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Explore;
