import React, { useEffect, useState } from 'react';
import { auth, provider } from './firebase';
import { signInWithPopup, signOut } from 'firebase/auth';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [loading, setLoading] = useState(true);

  const handleLogin = async () => {
    const result = await signInWithPopup(auth, provider);
    setUser(result.user);
  };

  const handleLogout = () => {
    signOut(auth).then(() => setUser(null));
  };

  useEffect(() => {
    const fetchNFTs = async () => {
      if (!user) return;

      const token = await user.getIdToken();
      const res = await fetch('/api/nfts', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      const data = await res.json();
      setNfts(data);
      setLoading(false);
    };

    fetchNFTs();
  }, [user]);

  return (
    <div>
      <header style={{ padding: '1rem', background: '#222', color: '#fff', textAlign: 'center' }}>
        <h1>NFT Marketplace</h1>
        {!user ? (
          <button onClick={handleLogin}>Sign in with Google</button>
        ) : (
          <div>
            <p>Welcome, {user.displayName}</p>
            <button onClick={handleLogout}>Logout</button>
          </div>
        )}
      </header>

      <main style={{ padding: '2rem' }}>
        {loading ? (
          <p>Loading NFTs...</p>
        ) : (
          <div style={{ display: 'flex', gap: '2rem', justifyContent: 'center' }}>
            {nfts.map((nft) => (
              <div key={nft.id} style={{ padding: '1rem', boxShadow: '0 2px 5px rgba(0,0,0,0.1)', borderRadius: 8 }}>
                <h3>{nft.name}</h3>
                <p>Price: {nft.price}</p>
                {nft.image && <img src={nft.image} alt={nft.name} style={{ width: 150 }} />}
                <button style={{ marginTop: '1rem' }}>Buy Now</button>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
